// ------------------------------------------
var setVisitorName = function (name) {
  var inputUserIdName = "faceAuth_name";
  if(!name) { return false};
  var inputVisitorName = document.getElementById(inputUserIdName);
  if(!inputVisitorName) { console.log("Could not detect username textfield"); }
  inputVisitorName.value = name;
}

// ------------------------------------------
// For version detection, set to min. required Flash Player version, or 0 (or 0.0.0), for no version detection.
var swfVersionStr = "11.1.0";
// To use express install, set to playerProductInstall.swf, otherwise the empty string.
var flashvars = {};
var params = {};
params.quality = "high";
params.bgcolor = "#cccccc";
params.allowscriptaccess = "always";
params.allowfullscreen = "true";
var attributes = {};
attributes.id = "faceAuthFlex";
attributes.name = "faceAuthFlex";
attributes.align = "middle";
swfobject.embedSWF(
    "http://www.panierter-pinguin.de/mmt/faceauth/faceAuthFlex-debug/faceAuthFlex.swf", "flashContent",
    "600", "550",
    swfVersionStr, null,
    flashvars, params, attributes);
// JavaScript enabled so display the flashContent div in case it is not replaced with a swf object.
swfobject.createCSS("#flashContent", "display:block;text-align:left;");

